import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const TicketSchema = Yup.object().shape({
  price: Yup.number().positive('Price must be positive').required('Required')
});

function EventDetail({ user }) {
  const { id } = useParams();
  const [event, setEvent] = useState(null);

  useEffect(() => {
    fetchEvent();
  }, [id]);

  const fetchEvent = async () => {
    const response = await fetch(`/events/${id}`);
    const data = await response.json();
    setEvent(data);
  };

  const handleBookTicket = async (values, { setSubmitting, resetForm }) => {
    try {
      const response = await fetch('/tickets', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...values,
          user_id: user.id,
          event_id: parseInt(id)
        })
      });

      if (response.ok) {
        alert('Ticket booked successfully!');
        resetForm();
      }
    } catch (error) {
      console.error('Error booking ticket:', error);
    }
    setSubmitting(false);
  };

  if (!event) return <div>Loading...</div>;

  return (
    <div>
      <div className="page-header">
        <h1>{event.title}</h1>
      </div>
      {event.image && (
        <img 
          src={event.image} 
          alt={event.title}
          style={{
            width: '100%',
            maxWidth: '600px',
            height: '300px',
            objectFit: 'cover',
            borderRadius: '8px',
            margin: '0 auto 2rem auto',
            display: 'block'
          }}
        />
      )}
      <div className="profile-header">
        <div className="profile-info">
          <p><strong>Date:</strong> {new Date(event.date).toLocaleString()}</p>
          <p><strong>Location:</strong> {event.location}</p>
        </div>
      </div>

      {user.role === 'customer' && (
        <div className="form-container">
          <h3>Book Your Ticket</h3>
          <Formik
            initialValues={{ price: '' }}
            validationSchema={TicketSchema}
            onSubmit={handleBookTicket}
          >
            {({ isSubmitting }) => (
              <Form>
                <div className="form-group">
                  <label htmlFor="price">Ticket Price</label>
                  <Field type="number" step="0.01" name="price" />
                  <ErrorMessage name="price" component="div" className="error" />
                </div>
                <button type="submit" disabled={isSubmitting} className="btn">
                  {isSubmitting ? 'Booking...' : 'Book Ticket'}
                </button>
              </Form>
            )}
          </Formik>
        </div>
      )}
    </div>
  );
}

export default EventDetail;
