import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const TicketSchema = Yup.object().shape({
  price: Yup.number().positive('Price must be positive').required('Required')
});

function EventDetail({ user }) {
  const { id } = useParams();
  const [event, setEvent] = useState(null);

  useEffect(() => {
    fetchEvent();
  }, [id]);

  const fetchEvent = async () => {
    const response = await fetch(`/events/${id}`);
    const data = await response.json();
    setEvent(data);
  };

  const handleBookTicket = async (values, { setSubmitting, resetForm }) => {
    try {
      const response = await fetch('/tickets', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...values,
          user_id: user.id,
          event_id: parseInt(id)
        })
      });

      if (response.ok) {
        alert('Ticket booked successfully!');
        resetForm();
      }
    } catch (error) {
      console.error('Error booking ticket:', error);
    }
    setSubmitting(false);
  };

  if (!event) return (
    <div className="loading-container">
      <div className="loading-spinner"></div>
      <p>Loading event details...</p>
    </div>
  );

  return (
    <div className="event-detail-page">
      <div className="event-hero">
        {event.image && (
          <div className="hero-image-container">
            <img 
              src={event.image} 
              alt={event.title}
              className="hero-image"
            />
            <div className="hero-overlay">
              <div className="hero-content">
                <h1 className="event-title">{event.title}</h1>
                <div className="event-meta">
                  <span className="meta-item">
                    <span className="meta-icon">üìÖ</span>
                    {new Date(event.date).toLocaleDateString('en-US', { 
                      weekday: 'long', 
                      year: 'numeric', 
                      month: 'long', 
                      day: 'numeric' 
                    })}
                  </span>
                  <span className="meta-item">
                    <span className="meta-icon">üìç</span>
                    {event.location}
                  </span>
                  <span className="meta-item">
                    <span className="meta-icon">üïí</span>
                    {new Date(event.date).toLocaleTimeString('en-US', { 
                      hour: '2-digit', 
                      minute: '2-digit' 
                    })}
                  </span>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      <div className="event-content">
        <div className="content-grid">
          <div className="main-content">
            <div className="description-card">
              <h2>About This Event</h2>
              {event.description ? (
                <p className="event-description">{event.description}</p>
              ) : (
                <p className="no-description">No description available for this event.</p>
              )}
            </div>

            <div className="details-card">
              <h3>Event Details</h3>
              <div className="details-grid">
                <div className="detail-item">
                  <span className="detail-label">üìÖ Date & Time</span>
                  <span className="detail-value">{new Date(event.date).toLocaleString()}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">üìç Location</span>
                  <span className="detail-value">{event.location}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">üé´ Event Type</span>
                  <span className="detail-value">General Admission</span>
                </div>
              </div>
            </div>
          </div>

          {user.role === 'customer' && (
            <div className="booking-sidebar">
              <div className="booking-card">
                <h3>üé´ Book Your Ticket</h3>
                <p className="booking-subtitle">Secure your spot at this amazing event</p>
                <Formik
                  initialValues={{ price: '' }}
                  validationSchema={TicketSchema}
                  onSubmit={handleBookTicket}
                >
                  {({ isSubmitting }) => (
                    <Form className="booking-form">
                      <div className="form-group">
                        <label htmlFor="price">üí∞ Ticket Price ($)</label>
                        <Field 
                          type="number" 
                          step="0.01" 
                          name="price" 
                          placeholder="Enter your price"
                          className="price-input"
                        />
                        <ErrorMessage name="price" component="div" className="error" />
                      </div>
                      <button type="submit" disabled={isSubmitting} className="book-btn">
                        {isSubmitting ? (
                          <span>üîÑ Booking...</span>
                        ) : (
                          <span>üé´ Book Ticket Now</span>
                        )}
                      </button>
                      <p className="booking-note">üí° Set your own price for this event</p>
                    </Form>
                  )}
                </Formik>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default EventDetail;
