import React, { useState, useEffect } from 'react';

function Profile({ user }) {
  const [tickets, setTickets] = useState([]);

  useEffect(() => {
    fetchUserTickets();
  }, [user.id]);

  const fetchUserTickets = async () => {
    const response = await fetch(`/users/${user.id}/tickets`);
    const data = await response.json();
    setTickets(data);
  };

  const handleCancelTicket = async (ticketId) => {
    try {
      const response = await fetch(`/tickets/${ticketId}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        fetchUserTickets();
      }
    } catch (error) {
      console.error('Error canceling ticket:', error);
    }
  };

  return (
    <div>
      <div className="page-header">
        <h1>My Profile</h1>
      </div>

      <div className="profile-header">
        <div className="profile-info">
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Role:</strong> {user.role}</p>
        </div>
      </div>

      <div className="tickets-section">
        <h2>My Tickets</h2>
        {tickets.length === 0 ? (
          <div className="empty-state">
            <h3>No tickets yet!</h3>
            <p>Start exploring events and book your first ticket.</p>
          </div>
        ) : (
          <div className="tickets-list">
            {tickets.map(ticket => (
              <div key={ticket.id} className="ticket-item">
                <div className="ticket-info">
                  <h3>{ticket.event.title}</h3>
                  <p><strong>Date:</strong> {new Date(ticket.event.date).toLocaleString()}</p>
                  <p><strong>Location:</strong> {ticket.event.location}</p>
                  <p className="price-tag"><strong>Price:</strong> ${ticket.price}</p>
                </div>
                <button 
                  onClick={() => handleCancelTicket(ticket.id)} 
                  className="btn btn-danger"
                >
                  Cancel Ticket
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Profile;
