import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const EventSchema = Yup.object().shape({
  title: Yup.string().required('Required'),
  date: Yup.date().required('Required'),
  location: Yup.string().required('Required')
});

function Events({ user }) {
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchEvents();
  }, []);

  useEffect(() => {
    if (searchTerm === '') {
      setFilteredEvents(events);
    } else {
      const filtered = events.filter(event =>
        event.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        event.location.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (event.description && event.description.toLowerCase().includes(searchTerm.toLowerCase()))
      );
      setFilteredEvents(filtered);
    }
  }, [events, searchTerm]);

  const fetchEvents = async () => {
    const response = await fetch('/events');
    const data = await response.json();
    setEvents(data);
    setFilteredEvents(data);
  };

  const handleCreateEvent = async (values, { setSubmitting, resetForm }) => {
    try {
      const response = await fetch('/events', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...values, created_by: user.id })
      });

      if (response.ok) {
        fetchEvents();
        resetForm();
        setShowForm(false);
      }
    } catch (error) {
      console.error('Error creating event:', error);
    }
    setSubmitting(false);
  };

  const handleDeleteEvent = async (eventId) => {
    try {
      const response = await fetch(`/events/${eventId}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        fetchEvents();
      }
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  return (
    <div className="events-page">
      <div className="hero-section-short">
        <h1>Discover Amazing Events</h1>
        <p>Find and book tickets for the best events in your area</p>
      </div>

      <div className="events-header">
        <h2>All Events</h2>
        <div className="header-controls">
          {user.role === 'customer' && (
            <input
              type="text"
              placeholder="Search events..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          )}
          {user.role === 'admin' && (
            <button onClick={() => setShowForm(!showForm)} className="btn create-btn">
              {showForm ? 'Cancel' : 'Create Event'}
            </button>
          )}
        </div>
      </div>

      {showForm && user.role === 'admin' && (
        <div className="form-container">
          <h3>Create New Event</h3>
          <Formik
            initialValues={{ title: '', date: '', location: '', description: '', image: '' }}
            validationSchema={EventSchema}
            onSubmit={handleCreateEvent}
          >
            {({ isSubmitting }) => (
              <Form>
                <div className="form-group">
                  <label htmlFor="title">Title</label>
                  <Field type="text" name="title" />
                  <ErrorMessage name="title" component="div" className="error" />
                </div>
                <div className="form-group">
                  <label htmlFor="date">Date</label>
                  <Field type="datetime-local" name="date" />
                  <ErrorMessage name="date" component="div" className="error" />
                </div>
                <div className="form-group">
                  <label htmlFor="location">Location</label>
                  <Field type="text" name="location" />
                  <ErrorMessage name="location" component="div" className="error" />
                </div>
                <div className="form-group">
                  <label htmlFor="description">Description</label>
                  <Field as="textarea" name="description" rows="3" placeholder="Event description..." />
                  <ErrorMessage name="description" component="div" className="error" />
                </div>
                <div className="form-group">
                  <label htmlFor="image">Image URL</label>
                  <Field type="url" name="image" placeholder="https://example.com/image.jpg" />
                  <ErrorMessage name="image" component="div" className="error" />
                </div>
                <button type="submit" disabled={isSubmitting} className="btn">
                  {isSubmitting ? 'Creating...' : 'Create Event'}
                </button>
              </Form>
            )}
          </Formik>
        </div>
      )}


      <div className="events-container">
        {filteredEvents.map(event => (
          <div key={event.id} className="event-item">
            {event.image && (
              <img 
                src={event.image} 
                alt={event.title}
                className="event-image"
              />
            )}
            <div className="event-details">
              <h3 className="event-title">{event.title}</h3>
              <p className="event-date">{new Date(event.date).toLocaleDateString()}</p>
              <p className="event-location">{event.location}</p>
              {event.description && (
                <p className="event-description">{event.description}</p>
              )}
            </div>
            <div className="event-actions">
              <Link to={`/events/${event.id}`} className="btn view-btn">
                View
              </Link>
              {user.role === 'admin' && (
                <button 
                  onClick={() => handleDeleteEvent(event.id)} 
                  className="btn delete-btn"
                >
                  Delete
                </button>
              )}
            </div>
          </div>
        ))}
        {filteredEvents.length === 0 && searchTerm && (
          <div className="no-results">
            <p>No events found matching "{searchTerm}"</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default Events;
