import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const EventSchema = Yup.object().shape({
  title: Yup.string().required('Required'),
  date: Yup.date().required('Required'),
  location: Yup.string().required('Required')
});

function Events({ user }) {
  const [events, setEvents] = useState([]);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    const response = await fetch('/events');
    const data = await response.json();
    setEvents(data);
  };

  const handleCreateEvent = async (values, { setSubmitting, resetForm }) => {
    try {
      const response = await fetch('/events', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(values)
      });

      if (response.ok) {
        fetchEvents();
        resetForm();
        setShowForm(false);
      }
    } catch (error) {
      console.error('Error creating event:', error);
    }
    setSubmitting(false);
  };

  const handleDeleteEvent = async (eventId) => {
    try {
      const response = await fetch(`/events/${eventId}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        fetchEvents();
      }
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  return (
    <div>
      <div className="page-header">
        <h1>Discover Amazing Events</h1>
      </div>
      <div className="header-actions">
        <div></div>
        {user.role === 'admin' && (
          <button onClick={() => setShowForm(!showForm)} className="btn">
            {showForm ? 'Cancel' : 'Create Event'}
          </button>
        )}
      </div>

      {showForm && user.role === 'admin' && (
        <div className="form-container">
          <h3>Create New Event</h3>
          <Formik
            initialValues={{ title: '', date: '', location: '', image: '' }}
            validationSchema={EventSchema}
            onSubmit={handleCreateEvent}
          >
            {({ isSubmitting }) => (
              <Form>
                <div className="form-group">
                  <label htmlFor="title">Title</label>
                  <Field type="text" name="title" />
                  <ErrorMessage name="title" component="div" className="error" />
                </div>
                <div className="form-group">
                  <label htmlFor="date">Date</label>
                  <Field type="datetime-local" name="date" />
                  <ErrorMessage name="date" component="div" className="error" />
                </div>
                <div className="form-group">
                  <label htmlFor="location">Location</label>
                  <Field type="text" name="location" />
                  <ErrorMessage name="location" component="div" className="error" />
                </div>
                <div className="form-group">
                  <label htmlFor="image">Image URL</label>
                  <Field type="url" name="image" placeholder="https://example.com/image.jpg" />
                  <ErrorMessage name="image" component="div" className="error" />
                </div>
                <button type="submit" disabled={isSubmitting} className="btn">
                  {isSubmitting ? 'Creating...' : 'Create Event'}
                </button>
              </Form>
            )}
          </Formik>
        </div>
      )}

      <div className="events-grid">
        {events.map(event => (
          <div key={event.id} className="event-card">
            {event.image && (
              <img 
                src={event.image} 
                alt={event.title}
                style={{
                  width: '100%',
                  height: '200px',
                  objectFit: 'cover'
                }}
              />
            )}
            <div className="event-card-content">
              <h3>{event.title}</h3>
              <p><strong>Date:</strong> {new Date(event.date).toLocaleString()}</p>
              <p><strong>Location:</strong> {event.location}</p>
              <div className="card-actions">
                <Link to={`/events/${event.id}`} className="btn">
                  View Details
                </Link>
                {user.role === 'admin' && (
                  <button 
                    onClick={() => handleDeleteEvent(event.id)} 
                    className="btn btn-danger"
                  >
                    Delete
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Events;
